mapper.select:
select 
from fee
where row_status =0

  and operate_date >= #{start}


  and operate_date <= #{end}


  and operate_date = #{operateDate}


  and operate_thing like CONCAT(#{operateThing},'%')


  and beneficiary like CONCAT(#{beneficiary},'%')


  and pay_type = #{payType}


  and fee_type = #{feeType}


  and belong = #{belong}


  and payer like CONCAT(#{payer},'%')


  order by id DESC


  order by #{column} #{sort}


  limit #{offset},#{pageSize}

select count(1)
from fee
where row_status =0

  and operate_date >= #{start}


  and operate_date <= #{end}


  and operate_date = #{operateDate}


  and operate_thing like CONCAT(#{operateThing},'%')


  and beneficiary like CONCAT(#{beneficiary},'%')


  and pay_type = #{payType}


  and fee_type = #{feeType}


  and belong = #{belong}


  and payer like CONCAT(#{payer},'%')

select distinct(operate_thing) from fee where row_status =0
and operate_thing like CONCAT(#{operateThing},'%')
limit #{offset},#{pageSize}

select distinct(beneficiary) from fee where row_status =0
and beneficiary like CONCAT(#{beneficiary},'%')
limit #{offset},#{pageSize}

select distinct(payer) from fee where row_status =0
and payer like CONCAT(#{payer},'%')
limit #{offset},#{pageSize}
