mapper.select:
select 
from purchase
where row_status =0

  and operate_date >= #{start}


  and operate_date <= #{end}


  and operate_date = #{operateDate}


  and product_category = #{productCategory}


  and product_name like CONCAT(#{productName},'%')


  and car_type like CONCAT(#{carType},'%')


  and belong = #{belong}


  and manager like CONCAT(#{manager},'%')


  and supplier_company_name like CONCAT(#{supplierCompanyName},'%')


  and supplier_phone like CONCAT(#{supplierPhone},'%')


  and supplier_person like CONCAT(#{supplierPerson},'%')


  and is_paid = #{isPaid}


  order by id DESC


  order by #{column} #{sort}


  limit #{offset},#{pageSize}

select count(1)
from purchase
where row_status =0

  and operate_date >= #{start}


  and operate_date <= #{end}


  and operate_date = #{operateDate}


  and product_category = #{productCategory}


  and product_name like CONCAT(#{productName},'%')


  and car_type like CONCAT(#{carType},'%')


  and belong = #{belong}


  and manager like CONCAT(#{manager},'%')


  and supplier_company_name like CONCAT(#{supplierCompanyName},'%')


  and supplier_phone like CONCAT(#{supplierPhone},'%')


  and supplier_person like CONCAT(#{supplierPerson},'%')


  and is_paid = #{isPaid}

select distinct(product_name) from purchase where row_status =0
and product_name like CONCAT(#{productName},'%')
limit #{offset},#{pageSize}

select distinct(car_type) from purchase where row_status =0
and car_type like CONCAT(#{carType},'%')
limit #{offset},#{pageSize}

select distinct(manager) from purchase where row_status =0
and manager like CONCAT(#{manager},'%')
limit #{offset},#{pageSize}

select distinct(supplier_company_name) from purchase where row_status =0
and product_name like CONCAT(#{productName},'%')
limit #{offset},#{pageSize}
